<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Account extends CI_Model {    function __construct() {        parent::__construct();    }    function check_email($email = '', $id = 0) {        $this->db->select('*');        if ($id > 0)            $this->db->where('id!=', $id);        $this->db->where('email', $email);        $query = $this->db->get(TBL_MASTER);        if ($query->num_rows() > 0) {            return $query->result();        }        return NULL;    }    function update_account($data, $id) {        $this->db->where('id', $id);        $this->db->update(TBL_MASTER, $data);        return $id;    }    function get_account($id) {        $this->db->select('s.*');        $this->db->where('s.id', $id);        $query = $this->db->get(TBL_MASTER . ' s');        if ($query->num_rows() > 0) {            return $query->row();        }        return NULL;    }   function remove_data($id, $field, $table) {        $this->db->where($field, $id);        return $this->db->delete($table);    }}