<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Common extends CI_Model {    function __construct() {        parent::__construct();    }    function add_info($data, $table=TBL_MASTER) {        $this->db->insert($table, $data);        return $this->db->insert_id();    }    function update_info($id) {          $post_data = array( "is_active" => 'active', );        $this->db->where('id',$id);        $this->db->update(TBL_MASTER, $post_data);        return true;    }        function check_email($email) {        $this->db->select('id');        $this->db->where('email', $email);      $query = $this->db->get(TBL_MASTER);        return    $query->num_rows();        }        function check_verify_email($id,$hash) {        $this->db->select('id');        $this->db->where('id', $id);         $this->db->where('emailhash', $hash);      $query = $this->db->get(TBL_MASTER);        return    $query->num_rows();        }   function fetch_apicontent($id) {        $this->db->select("title,author,points,story_text");        $this->db->where('userid',$id);         $this->db->limit(10);        $query = $this->db->get(TBL_APIDATA);      // echo $this->db->last_query();         if ($query->num_rows() > 0) {            return $query->result();        }        return array();    }}